function ageCh($cons,$ddis){
	$cons2="";
	for($ji=date("Y")-31+1;$ji<=date("Y")-20+1;$ji++){
		if(strpos($cons,substr($ji,2,2))>0){
			$cons2="ok";
		}
		if($ji%12==$ddis){
			$lastn=$ji;
		}
	}
	if($cons2=="ok"){$cons2=$cons;}
	else{
		$cons3=$cons;
		for($ji=43;$ji<=95;$ji++){
			$cons3=str_replace($ji,"@#@",$cons3);
		}
		$cons3=explode("@#@",$cons3);
		$cons2=$cons3[0];

		for($ji=1;$ji<count($cons3);$ji++){
			$cons2=$cons2.substr($lastn-(12*(count($cons3)-$ji-1)),2,2).$cons3[$ji];
		}
	}
	return $cons2;
}


