insert into J004(DB_check, DB_data, DB_express, DB_num, DB_que) values('Y', '이 괘의 건강적 특성은 균형적인 부분이 잘 발달하여<BR>있으니 기본적으로 건강하나, 염증이나 스트레스등의<BR>잔 질환정도는 지금도 주의해야&nbsp; 한다고 판단할 수 있<BR>습니다.&nbsp;<BR><BR>지금당장 큰 병은 없습니다. 작은 상처나 염증정도의 <BR>생활에서 쉽게 발생할 수 있는 정도의 부상이나 몸살<BR>정도입니다.&nbsp;주변을 청결히 하고 위생에 신경을 쓰도<BR>록 해야 합니다.', '34', '20', '화뢰서합(火雷&#22124;&#21969;</SPAN>) ');insert into J004(DB_check, DB_data, DB_express, DB_num, DB_que) values('Y', '이 괘가 의미하는 것은 솥을 의미하는 것으로 건강상<BR>에서는 꽤&nbsp;희망적이라 할 수 있습니다. 그러나&nbsp;고정적<BR>인 특성을 가지고 있으므로&nbsp;활동과 운동에 대한 필요<BR>성이 있다 할 수 있겠습니다. &nbsp;<BR><BR>현재 건강에 큰 이상은 없겠습니다. 그러나 과잉섭취, <BR>운동 부족에는&nbsp;주의해야 할 것입니다. 지금 환자는 곧 <BR>건강을 찾을 수 있습니다. <BR><BR>모든 일은 체력이 따라야 하는 법이니 술 담배를 멀리 하고 가벼운 운동이라도 시작하도록 해<BR>야 합니다. <BR><BR>', '35', '21', '화풍정(火風鼎) ');insert into J004(DB_check, DB_data, DB_express, DB_num, DB_que) values('Y', '이 괘의 건강에 대한 해석은 상하의 균형이 맞지 않는<BR>것을 의미합니다. 대개 빈혈이나 하체빈약등의 문제를<BR>생각할 수 있습니다만, 큰 병은 없어도 약한 체질이므<BR>로 체질 개선이 필요한 정도입니다.<BR><BR>별 다른 이상이 없음에도 몸이 자꾸 마르고 피로가 심<BR>합니다. 지나치게 신경을 쓴 탓입니다. <BR><BR>병이 생기기 보다 피로로 인한 컨디션의 이상에 대해 <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 더 신경을 써야 하고자 하는 일에도 능률이 따른다고 <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 볼 수 있겠습니다. ', '36', '22', '화수미제(火水未濟) ');insert into J004(