insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '전반적으로 건강한 한 해를 맞이하는 운이라 할 수 있<BR>겠습니다.&nbsp;대체적으로 활동적인 편이며 신체리듬도 기<BR>본적으로 상승하는 운이라 할 수 있으니 큰 탈은 없다<BR>할 수 있습니다. <BR><BR>다만, 환절기나 겨울철에는 호흡기질환을 주의해야 하<BR>니 잔병이나 컨디션저하로 인한 몸살정도를 경계해야 <BR>합니다.', '', '9');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '적절한 운동으로 건강을 돌보는 것은 좋으나 지나치게<BR>몸을 혹사하거나 무리한 운동으로 부상당하지 않도록<BR>주의가 필요합니다. 건강에는 이상이 없으니 적절한<BR>주의로 충분하지만 스스로의 실수로 부상당하는 것은<BR>경계하는 것이 좋습니다. ', '', '10');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '신체리듬이 안정화 되고 활력적인 한 해를 맞이했다 <BR>할 수 있겠습니다. <BR><BR>큰 어려움 없이 일사을 영위하는 여유로움이 엿보인다<BR>할 수 있겠습니다. <BR><BR>그러나 먼 이동이나 갑작스러운 환경, 날씨의 변화는 <BR>리듬에 무리를 가져 올 수 있으니 주의가 필요하다 할<BR>수 있겠습니다. ', '', '11');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강운은 매우 안정적이므로 큰 이상을 발견하기는 어<BR>렵다 할 수 있겠습니다. <BR><BR>활동력이 상승하므로 일상생활의 진행에 무리가 없겠<BR>으니 대체적으로 건강한 한 해를 예상할 수 있겠습니<BR>다.<BR><BR>다만&nbsp;체력이 저하되어 일시적으로 몸살이 나거나 컨디<BR>션의 난조가 발생할 수 있는 정도라 할 수 있겠습니다. ', '', '12');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '전반적으로는 건강한 편이라 할 수 있겠습니다만, 호흡<BR>기나 과로로 인한 몸살정도를 주의함이 좋겠습니다. <BR><BR>큰 이상을 예상할 수 있는 정도는 아니지만 불규칙한 <BR>일상과 갑작스러운 환경의 변화로 인한 신체에 스트레<BR>스를 주게 되어 나타나는 증상이 있을 수 있겠습니다.<BR><BR>미리 예방하여 일상에 지장이 없도록 하는 것이 현명<BR>하겠습니다. ', '', '13');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '감기나 불면증등 가벼운 질병이 있을 수 있는 정도의&nbsp;<BR>운이라 할 수 있겠습니다.&nbsp;<BR><BR>대체적으로는&nbsp;건강한 편이지만, 신경이 예민하고 갑작<BR>스러운&nbsp;환경의 변화에 어려움을 겪을 수 있는 정도입<BR>니다.<BR>&nbsp;<BR>&nbsp;', '', '14');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '수면부족이나 두통등의 일상생활에 큰 지장을 주는 정<BR>도는 아니라 할 수 있는 정도로 미약한 정도의 지장을<BR>예상할 수 있습니다. <BR><BR>그러나 능률이 떨어지고 컨디션에 지장을 줄 수 있으<BR>니 평소 건강에 세심한 관리를 해야 할 것입니다. <BR><BR>', '', '15');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강은 위장이나 대장등의 소화기쪽이 약해 질 수 있<BR>는 운이라 할 수 있겠습니다.&nbsp;&nbsp;<BR><BR>일상적인 활동력은 오히려 늘어나므로 외관상 건강에<BR>큰 이상이 없다고 여겨지지만 리듬의 균형이 맞지 않<BR>아서 생기는 문제가 예상되니 규칙적인 생활이 따라야<BR>하겠습니다. ', '', '16');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강에 큰 이상은 없지만 지나치게 과신하여 무리하지<BR>말아야 합니다. <BR><BR>특히 밤샘이나 과음과 같은 이유로&nbsp;신체리듬의 이상이 <BR>발생 하면 컨디션의 문제부터 여러 불편을 초래한다 <BR>할 수 있으니 자신의 건강을 과신하여 행동하지 말아<BR>야 합니다. <BR><BR>건강에 이상은 없을 운이지만 관리에 따라 달라질 수&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 있는 여지가 있다 할 수 있겠습니다. ', '', '17');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강에는 큰 이상이 없지만 보행이나 외부활동중에 발<BR>생하기 쉬운 부상으로 다치는 일을 주의해야 합니다.<BR><BR>활동과 변화가 빈번하게 발생하므로 부주의로 인한 외<BR>부충격에 주의해야 합니다. <BR><BR>기타 환절기의 기후변화로 인한 몸살이나 감기와 같은<BR>것은 주의해야 합니다. ', '', '18');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강에 중대한 변화나 이상은 없다 할 수 있겠습니다<BR>만 몸살이나 감기와 같은 잔병이 발생할 수 있는 여지<BR>는 있다 할 수 있겠습니다. <BR><BR>갑작스런 날씨변화나 외부환경의 변화에 주의하며 규<BR>칙적인 생활로 미리 예방하는 지혜가 필요하다 하겠습<BR>니다. ', '', '19');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강에 큰 이상은 없겠습니다만, 규칙적이지 못한 일<BR>상생활로 인해 발생할 수 있는 수면부족이나 위장의<BR>장애에 대해서는 주의가 필요하다 할 수 있겠습니다.<BR><BR>특히 습도가 높거나 여름철에는 음식섭취에 주의하지<BR>않으면 배탈이 나거나 하는 일로 고생할 수 있겠습니<BR>다. <BR><BR>위장에 무리가 가지 않도록 하며 가급적 술자리는 피<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 하는 것이 좋겠습니다. ', '', '20');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '중대한 질병이나 건강의 문제가 심각하게 나타나지는<BR>않습니다만, 주의가 필요합니다. 건강은 지킬 수 있을 <BR>때 지켜야 합니다. <BR><BR>일상생활에 큰 불편을 느끼지 못한다 해도 바른 자세<BR>를 갖기 위한 노력이 있어야 할 것이며, 평소에 체력유<BR>지를 위한 운동과 같은 노력이 있어야 합니다.', '', '21');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강운과 관계된 신체의 컨디션 정도는 보편적인 정도<BR>이상의 높게 나타나니 건강에 큰 무리가 따르지는 않<BR>겠습니다. <BR><BR>그러나 여행이나 먼 곳으로 이동하는 경우에 환경에<BR>적응하는 동안이나 시차등의 환경변화에 따라 몸살이<BR>나 음식으로 인한 고생이 있을 수 있으니 이런 점은<BR>주의해야 하겠습니다. ', '', '22');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '과식이나 철야작업등의 무리를 주의해야 합니다. 그러<BR>한 문제만 없다면 큰 어려움 없이 건강한 한 해를 지<BR>낼 수 있겠으나 무리와 리듬을 잃기 쉬우니 조심하는<BR>것이 좋겠습니다. ', '', '23');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강은 큰 문제가 없겠으나 여름철에는 음식섭취에 유<BR>의해야 하며, 겨울철에는 불면증을 주의해야 하겠습니<BR>다. <BR><BR>일상생활에 큰 지장을 주는 정도는 아니지만 방치하게<BR>되면 습관성 불면증이나 소화불량으로 이어질 수 있으<BR>니 신중하게 대처하여야 하며 평소에 규칙적인 생활을<BR>통해 방비해야 하겠습니다. ', '', '24');insert into S098(DB_check, DB_data, DB_express, DB_num) values('Y', '건강에 큰 이상은 없겠으나 나른하고 피곤하다거나&nbsp;<BR>불면증이 발생한다거나 소화가 잘 않되는 등의 변화가<BR>있을 수 있겠습니다.&nbsp;<BR><BR>대체적으로 환절기에 이러한 증상이 나타나기 쉬우며<BR>신경이 예민한 경우는 주기적일 수도 있겠습니다.&nbsp;<BR><BR>저녁에는 편안히 지내며 아침에 운동이나 활동을 통해&nbsp;<BR>에너지 순환을 도와주어야 한다 하겠습니다. &nbsp;', '', '25');