 insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천을귀인(天乙貴人)이란 길성중에서 가장 고귀한 성이며 지혜가 총명하고 글솜씨가 뛰어나고 성격이 밝다.', '천을귀인', '1');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천주귀인(天廚貴人)은  평생 의식이 풍부한 복록이 따른다. 명예를 얻고 재물을 모은다.', '천주귀인', '2');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천관귀인(天官貴人)은 이 귀인이 있는 주의 육친이 관직에 오른다. 단 흉살이 많이 있으면 나쁘게 작용한다.', '천관귀인', '3');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천복귀인(天福貴人)은 복과 덕을 상징하는 길성으로 다른 길성과 같이 있으면 그 의미가 더욱 좋아진다.', '천복귀인', '4');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '태극귀인(太極貴人)이란 태극은 시종(始終)을 의미하여 종래 성과를 거두는 기쁨을 암시한다.격국이 우수하면 입신양명(立身揚名)한다는 것이다.', '태극귀인', '5');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '월덕귀인(月德貴人)은 땅의 덕을 상징하는 길성으로 넉넉한 부동산을 소유하게 되고 물질의 풍요로움과 부귀한 인생을 보낸다.', '월덕귀인', '6');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '문창(文昌)은 학자를 상징하는 별로 머리가 총명하다. 학문에 뛰어난 재능이 있고 멋을 알아 풍류 또한 즐기는 타입이다. 문학가나 예술가가 많다.', '문창', '7');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '문곡(文曲) 학문에서의 출세를 나타내며 머리가 총명하고 글재주가 있습니다.', '문곡', '8');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '관귀학관(官貴學官)은 지혜가 밝고 학문이 뛰어나 교육자로 성공하거나 공무원이 되면 승직이 매우 빨라 입신출세(立身出世)한다는 길성으로 고위직에 오른다는 암시가 있습니다.', '관귀학관', '9');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '암록(暗祿)은 문학과 예술분야에서 동시에 이름을 날리며 불가사의한 복록을 누리며 역경에 처하여도 귀인이 도운다.', '암록', '10');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '금여록(金與祿)은 천성이 부드럽고 용모가 단정하며 재치가 있다. 남명은 후덕한 아내를 만나 가정을 화목하게 이루며 여명은 부하거나 아니면 귀하다.', '금여록', '11');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '학당(學堂)은 지성이 돋보이고, 교육자로 진로를 정하면 특히 길하다. 주중(柱中)에 흉이 있어도 제거하는 역할을 한다.', '학당', '12');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천의(天醫)는 하늘이 의술을 베풀어 준다는 길성으로 사람의 목숨을 구하는 직업, 즉 의사,약사,간호사의 직업을 가지면 좋다.', '천의', '13');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '괴강살(魁강殺)은 길성이 함께 있으면 부귀,권세를 누리게 되지만 흉살과 같이 있으면 납치,감금, 구속,단명등 흉한 암시가 있습니다.', '괴강살', '14');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '백호살(白虎殺)은 그 주에 해당하는 육친이 비명횡사하게 된다는 살이다.', '백호살', '15');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '원진살(怨嗔殺)은 두 사람의 뜻이 맞지 않아 서로 불평하고, 원망하고, 화를 내게 되어 가정의 불화가 끊임없이 일어나게 됩니다.', '원진살', '16');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '귀문관살(鬼門關)은 해당 육친이 신경성 질환을 앓거나 정신병을 앓고 있습니다.', '귀문관살', '17');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '홍염살(紅艶殺)은 외정(外情)이 두려울 지경에 이르기도 한다. 여명은 기생(妓生), 남명은 작첩(作妾)한다는 암시가 있고 이성에게 호감을 사는 매력이 따른다.', '홍염살', '18');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '양인살(羊刃殺)은 너무 왕성하여 생기는 살로 형벌과 관계가 있다. 불굴의 의지와 용감한 기상을 지녔다. 군인,경찰,의사,법관, 스포츠맨등에서 두각을 보인다.', '양인살', '19');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '공망살(空亡殺)은 공허하고 허망하게 된다는 암시가 있습니다.', '공망살', '20');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '상문살(喪門殺)은 환자가 발생하거나 악재가 겹치거나 상복을 입는 경우가 있습니다.', '상문살', '21');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '수옥살(囚獄殺)은 사방이 막힌곳에 감금되는 암시가 있다. 허나 군인이나 경찰의 사주에 이 살이 있으면 오히려 좋게 본다.', '수옥살', '22');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '급각살(急脚殺)은 갑자기 하체가 마비 되거나 이상이 있다. 다리를 못쓰게 되거나 해당 육친이 그러하다.', '급각살', '23');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '상처살(喪妻殺)은 남자에게 있는 살로 이 살이 있으면 아내가 일찍 타계한다는 암시가 있습니다.', '상처살', '24');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '상부살(喪夫殺)은 여자에게 있는 살로 이 살이 있으면 남편이 일찍 타계한다는 암시가 있습니다.', '상부살', '25');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '진신(進神)은 자신이 하고 싶은 일을 골라하니 즐겁다는 길성(吉星)으로 냉철하며 문장력이 뛰어나다. 혹 관재수가 동하기도 한다.', '진신', '26');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '효신살(梟神殺)이란 일찍 생모(生母)와 이별하거나 어머니가 인연이 없거나 아니면 모처(母妻)가 상호 불화하는 육신의 의미를 지닌다. 효조(梟鳥)란 올빼미를 가리키는데 자라서는 어미 새를 잡아 먹는다는 흉한 습성을 가진 새로서 집안에 올빼미나 부엉이의 그림이라든가 박제 등을 두지 않는 것이 좋다.', '효신살', '27');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '평두살(平頭殺)은 다툼이 잦은 경향을 보인다. 종교인이 될 팔자라 하여 혼담이 이뤄지지 않고 결혼해도 파혼하는 수가 생긴다. 또 종교나 역술에 심취하며 그와 같은 길로 나아가게 된다.', '평두살', '28');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '취명관(取命關)은 사당이나 절 등에 가면 떠도는 혼령에 의해 질병을 얻을 수 있다.', '취명관', '29');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '복성귀인(福星貴人)은 수복(壽福)을 뜻하는 길신이므로 일생 동안 복록이 따르는 편이며 부귀장수한다는 것이다. 복성귀인은 시지(時支)에 있으면 더욱 좋다.', '복성귀인', '30');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '금신(金神)은 불굴의 의지와 초지일관(初志一貫)을 암시하는 성분으로 일시(日時)에 겹치면 사법관으로 출세하거나 무관으로 장성의 지위에 오른다.', '금신', '31');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '여연살(女戀殺)은 배우자 몰래 다른 애인을 숨겨 둘 여지가 있으므로 망신을 살 우려가 있다.', '여연살', '32');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '곡각살(曲脚殺)은 사고나 병으로 인하여 수족을 못 쓰거나 절단되는 것을 말한다. 또 신경통 등으로 수족에 이상이 있는 것도 포함된다.', '곡각살', '33');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '일덕(日德)은 일덕은 성격이 착하고 자비(慈悲)스러우며 복록(祿)이 풍후(豊厚)하다.', '일덕', '34');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '의처살(疑妻殺)은 배우자에게 집착하는 경향이 있다. 내심 배우자의 이성 관계를 의심하기도 한다.', '의처살', '35');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '의부살(疑夫殺)은 배우자에게 집착하는 경향이 있다. 내심 배우자의 이성 관계를 의심하기도 한다.', '의부살', '36');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '간여지동(干支同)은 부부간 생사이별수를 겪게 된다는 것으로 각거(各居)하게 되며, 백호(白虎) 등의 흉살(凶殺)과 겹치면 사별(死別)하기도 하는 것이다.', '간여지동', '37');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '음인살(陰刃殺)은 독선적인 성품을 지닌다. 자신도 모르게 칼을 맞는다는 암시가 있어 남의 비방(誹謗)을 받기도 하며 재물에 실패수가 따르기 쉬우니 주의할 일이다. ', '음인살', '38');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '신병살(身病殺)은 병이 끊이지 않고, 늘 몸이 아프다는 암시가 있습니다.', '신병살', '39');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천일관(千日關)은 출생 3년 내에 경풍(驚風), 토(吐)의 액이 있습니다.', '천일관', '40');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '계비관(鷄飛關)은 가축을 살생하는 것을 보면 질병에 노출될 위험이 있습니다.', '계비관', '41');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '낙정관살(落井關殺)은 수재(水災)나 익수지환(溺水之患)을 당한다는 암시가 있다. 이 살을 가진 사람은 물로 인한 재난에 특별히 조심해야 하고 직업을 선택할 때도 수산업이나 제빙업, 생수사업 같은, 물을 대상으로 삼는 사업은 하지 않는 게 좋다고 한다.', '낙정관살', '42');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '재고귀인(財庫貴人)은 재물을 많이 모아 부자(富者)가 된다는 길신(吉神)이다. ', '재고귀인', '43');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '남연살(男戀殺)은 남자는 배우자 몰래 다른 애인을 숨겨 둘 여지가 있으므로 망신을 살 우려가 있다. ', '남연살', '44');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '뇌관살(腦關殺)은 벼락, 전기 등으로 인한 사고의 위험을 내포한다.', '뇌관살', '45');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '철쇄개금(鐵鎖開金)은 활인(活人)이나 의약계에 인연이 많다. 타인을 위해 적덕(積德)을 많이 쌓지 않으면 본인이나 배우자의 신체에 액을 당할 우려가 있다.', '철쇄개금', '46');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '관식동림(官食同臨)은 첫 아이를 난산(難産)하거나 양육하는데 어려움을 겪음을 암시하고, 혹은 타자양육(他子養育)을 하게 되는 수도 있다. 관식동림은 혼전임신(婚前姙娠)을 경험하게 되는 요인이 된다.', '관식동림', '47');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '현침살(懸針殺)은 의약업계, 군인, 양복점 등 의류제조업으로 성공하며 가사, 이·미용에도 솜씨를 발휘한다. 흉한 작용으로는 몸을 잘 다치고 심하면 총에 맞거나 칼에 찔려 죽고 교통사고도 당한다. 時에 현침이 겹치면 그 작용력이 무척 강하다.', '현침살', '48');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '교신성(交神星)은 무슨 일이든 타인과 같이 하지 못한다는 성좌(星座)로 생각은 깊으나 자아심이 강하다. 주변 환경이 마음에 들지 않을 때가 많고 자기 생각과 같지 않다.', '교신성', '49');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '양착살(陽錯殺)은 배우자와 불화하고 사별하며 상부극처(傷夫剋妻)하게 되는 것이니 재취하여도 해로하기 어렵다. 또 음란하고 색난이 따른다. 심하게는 상(喪) 중에 취첩(娶妾)하며 혼인하여도 곧 실패수(被折)가 따른다고 표현하기도 한다. 그 작용력에 있어서 취첩(陽差)는 피절(陰錯)보다 강한 점이 있다. ', '양착살', '50');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '음차살(陰差殺)은 배우자와 불화하고 사별하며 상부극처(傷夫剋妻)하게 되는 것이니 재취하여도 해로하기 어렵다. 또 음란하고 색난이 따른다. 심하게는 상(喪) 중에 취첩(娶妾)하며 혼인하여도 곧 실패수(被折)가 따른다고 표현하기도 한다. 그 작용력에 있어서 취첩(陽差)은 피절(陰錯)보다 강한 점이 있다. ', '음차살', '51');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '음양살(陰陽殺)은 배우자와 불화하고 사별하며 상부극처(傷夫剋妻)하게 되는 것이니 재취하여도 해로하기 어렵다. 또 음란하고 색난이 따른다. 심하게는 상(喪) 중에 취첩(娶妾)하며 혼인하여도 곧 실패수(被折)가 따른다고 표현하기도 한다. 그 작용력에 있어서 취첩(陽差)은 피절(陰錯)보다 강한 점이 있습니다.', '음양살', '52');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '비인살(飛刃殺)은 매사에 집중을 잘 하면서도 실증을 잘 낸다. 지속성이 없고, 변덕을 부리는 편이다. 시작은 있되, 끝이 없고, 성공과 실패가 반복되는 유형이다.', '비인살', '53');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '퇴신(退神)은 가치관이 일반인과는 다소 다르며, 타인들에게 두드러져 보이려는 성격을 지닌다. 속단하여 실행한 일로 뒤늦게 후회하기도 한다.', '퇴신', '54');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '칠살(七殺)은 충돌, 언쟁, 관재, 구설, 수술, 고립 등의 상황에 처하기 쉬우며 교통사고나 부부간 다툼이나 냉전, 이별의 기운이 강한 편이다.', '칠살', '55');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '복신(伏神)은 매사가 지연, 정체된다는 암시가 있다.', '복신', '56');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '철사관(鐵蛇關)은 돌림병이나 희귀병에 걸려 단명의 암시가 있다고 전해지므로 방역이나 예방에 철저해야 한다.', '철사관', '57');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '구추방해(九醜妨害)는 이성간의 혼탁상이 예견된다. 잦은 연애사로 가정풍파를 암시하는 성분이다. 이상의 간지가 시주에 있으면 그 자녀가 같은 성향을 보이게 된다.', '구추방해', '58');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '단장관(斷腸關)은 대장(大腸)이나 소장(小腸) 계의 질환으로 고생할 암시가 있다.', '단장관', '59');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '유하살(流霞殺)은 남자는 타향에서 객사(客死)하고 여자는 산후 사망할 위험이 있다는 것이다. 평생 모은 재산이 안개처럼 흩어져 버리는 아픔이 있기도 하고 중풍(中風)이 유전되기도 한다.', '유하살', '60');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천라지망(天羅地網)은 감금, 구속, 시비, 송사 등의 구설이 따를 소지가 있다. 여명(女命)은 공방수나 파혼을 당해보기도 하며 자녀궁에 흉사가 예고되기도 한다.', '천라지망', '61');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '육수(六秀)는 성격이 급하고 재치가 있는 반면, 자기의 이익을 먼저 생각하는 습성이 있으므로 독단적인 기질이 있으며 동업 등은 피하는 게 좋다.', '육수', '62');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '음욕살(淫浴殺)은 음흉하고 색정적인 기질을 내포할 가능성이 있다. 여성이 이에 해당되면 유흥가 등으로 진출할 소지가 있고, 남성의 경우는 재혼하는 사례가 많다.', '음욕살', '63');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '상관사궁(傷官死宮)은  남편을 추방한다는 뜻이니 심하면 상부(喪夫)하고 독수공방(獨守空房)하게 된다는 것인데 외견은 고고(孤高)하고 도도하다. ', '상관사궁', '64');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '상관욕궁(傷官浴宮)은 남편을 추방하고 자기주장을 하고 산다는 의미가 있으므로 그 남편이 부단히 집을 떠나 객지생활(客地生活)을 하게 되고, 또는 첩(妾)을 두기도 하며, 재혼(再婚)을 해도 부덕(夫德)을 기대하기 어렵다.', '상관욕궁', '65');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '장군전(將軍箭)은 죄를 지으면 그 대가를 필히 치르게 된다는 성분으로 자암살(紫暗殺)로 불리기도 한다. 지지가 형충되면 무기나 흉기에 의해 죽음을 당하기도 한다는 암시가 있습니다.', '장군전', '66');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '정록(正祿)은 일지에 록이 있으면 위인이 건전하고 자립심이 강하여 자수성가하며, 부모의 유산을 받지 않는 경우가 대부분이다.', '정록', '67');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '겁살(劫殺)이 들어오는 해이므로 물건이 기물이 강제로 압류당할 일이 생긴다. 남자는 직장이 흔들리고 여자는 남편이 흔들린다. 만약 자녀가 있다면 자녀를 조심하고 없다면 임신중 유산을 조심하라. 사업을 한다면 확장은 금물이며 질병을 조심하라. ', '겁살_disp', '68');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '재살(災殺)이 들어오는 해이므로 주위의 사람을 이용해 본인의 목적과 희망을 이룬뒤 잠적하여 숨어다니는 생활을 하게 된다. 사업하는 사람은 자칫 부도를 내고 숨어다니거나 감옥에 갇히는 경우가 생긴다. 공직에 있는 자는 명예를 실추하게 되고 일반인은 사고수가 있어 몸에 화액을 면키 어려우니 모쪼록 자중하기 바란다.', '재살_disp', '69');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천살(天殺)이 들어오는 해이므로 천재지변 앞에서 몸을 떠는 암시이다. 명예가 상승되고 사업을 확장하고픈 마음이 들지만 그만큼의 이익을 볼수 없다. 위험한 장소에는 가지 않는게 좋다.', '천살_disp', '70');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '지살(地殺)이 들어오는 해이므로 이사, 변동수가 있다. 해외 여행수도 있다. 새로운 직장으로 옮기게 되거나 새로운 사업을 하게 되며 여기 저기 바쁘게 돌아다니면 이익을 볼수있다.', '지살_disp', '71');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '연살(年殺)이 들어오는 해이므로 주색에 탐닉하게 되고 남자는 바람을 피우게 되고 여자는 가출을 하게 된다. 미혼자는 결혼 상대를 만나며 기혼자는 이별수가 있습니다.', '연살_disp', '72');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '월살(月殺)이 들어오는 해이므로 되는 일이 없고 가슴이 답답하며 발전보다는 후퇴가 승진보다는 좌천이 예상되고 집안이 시끄러워 진다.', '월살_disp', '73');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '망신살(亡身殺)이 들어오는 해이므로 이익은 챙길수 있어도 명예가 실추되며 본인의 추한 모습을 모두 드러내 놓게 되며 여자는 산부인과 출입이 잦아지고 부인병에 걸릴 확률이 많다.', '망신살_disp', '74');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '장성살(將星殺)이 들어오는 해이므로 군인이나 경찰일 경우 나라를 위해 큰 일을 할 경우가 생기고 사업가는 해외출장이나 가정을 위해 헌신할 일이 생긴다.여자인 경우 남자를 대신하여 생활전선에 뛰어드는 경우가 있다. 미혼자인 경우는 좋은 인연을 만난다.', '장성살_disp', '75');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '반안살(攀鞍殺)이 들어오는 해이므로 무언가 새로 시작할 운이며 노력이 뒷받참되면 성공할 확률이 높다. 부동산을 매입하거나 재물이 늘어가는 징조가 보인다. 다만 이 운에서는 상복을 입을수 있다는 흉이 있습니다.', '반안살_disp', '76');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '역마살(驛馬殺)이 들어오는 해이므로 현재의 자리에서 벗어나 다른곳으로 옮기는 것이 길하다. 운수업,기자등은 바쁘게 다녀야 하니 길한 한해가 되지만 일반인은 멀리 떠나야 길하므로 가정과 이별의 쓸픔이 있습니다.', '역마살_disp', '77');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '육해살(六害살)이 들어오는 해이므로 신경계통의 질병을 않을 경우가 있고 남을 위해 열심히 노력하지만 알아주는 이가 없다. 쉽게 돈을 버는 일은 없고 신경을 쓰다 보니 질병을 얻게 된다. 종교를 찾게되는 경우가 많으며 누구에겐가 의지하고 싶은 마음이 생긴다.', '육해살_disp', '78');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '화개살(華蓋살)이 들어오는 해이므로 힘안들이고 횡재를 바라다 실패를 보며 이성을 찾아 헤메고 다니는 운이다. 남자는 주색잡기에 빠지는 경우가 허다하며 여자는 음기가 발동하여 바람이 날 공산이 크다.', '화개살_disp', '79');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '상문조객살(喪門弔客殺)이 들어오는 해이므로 가족이나 친척중에 누가 죽게되는 운을 암시하므로 상복을 입게되는 경우가 있다. 약하면 가정에 환자가 발생하거나 악재(惡災)가 일어날수 있습니다.', '상문조객살_disp', '80');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '고신살(孤神殺)이 들어오는 해이므로 아내와의 사이가 좋지 못하고 아내의 몸에 질병이 온다. 미혼이라면 되도록 혼인을 피하도록 하라.', '고신살_disp', '81');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '과숙살(寡宿殺)이 들어오는 해이므로 남편과의 사이가 좋지 못하고 남편의 몸에 재액이 온다. 미혼이라면 되도록 혼인을 피하도록 하라.', '과숙살_disp', '82');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천모살(天耗殺)이 들어오는 해이므로 윗사람의 모함이나 거짓말에 속아 재물을 손재하거나 명예를 실추당할 운이 있으니 조심하라.', '천모살_disp', '83');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '지모살(地耗殺)이 들어오는 해이므로 아랫사람이나 후배나 부하직원들의 모함이나 거짓말에 속아 재물을 손재하거나 명예를 실추당할 운이 있으니 조심하라.', '지모살_disp', '84');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '대모살(大耗殺)이 들어오는 해이므로 재물을 모아도 저축이 되지 않고 밑 빠진 독에 물 새듯 허무하게 사라져 버리며 무슨일을 도모하여도 실패수가 많다.', '대모살_disp', '85');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '소모살(小耗殺)이 들어오는 해이므로 재물을 모아도 저축이 되지 않고 밑 빠진 독에 물 새듯 허무하게 사라져 버리며 무슨일을 도모하여도 실패수가 많다. ', '소모살_disp', '86');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '격각살(隔角殺)이 들어오는 해이므로 이 살은 형벌을 암시하며 강제적인 구속을 당한다는 암시가 있으니 올해 한해는 혹 관청에 갈 일이 생기겠다. 음주운전을 유의하라.', '격각살_disp', '87');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '반음살(返吟殺)이 들어오는 해이므로  남자는 아내와 자식에게 해로움이 있으며 여자는 독수공방에 눈물이 마르지 않는다.', '반음살_disp', '88');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '복음살(伏吟殺)이 들어오는 해이므로 가정이나 집안이 시끄럽고 눈물을 흘릴일이 생기며 재산이 흩어지고 명예가 실추된다.', '복음살_disp', '89');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '병부살(病符殺)이 들어오는 해이므로 올 한해는 몸에 신병이 잦다. 미리 병원에 가 건강진단도 받아보는게 좋다.', '병부살_disp', '90');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '사부살(死符殺)이 들어오는 해이므로 싸움을 삼가하고 질병을 조심하라. 목숨이 위태로운 지경에 처할수도 있습니다.', '사부살_disp', '91');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '관부살(官符殺)이 들어오는 해이므로 경찰서나 관공서에 출입할 일이 생긴다.', '관부살_disp', '92');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '태음살(太陰殺)이 들어오는 해이므로 매사에 의욕이 떨어지고 주색잡기에 빠질 공산이 크다.', '태음살_disp', '93');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '세파살(歲破殺)이 들어오는 해이므로 직장인은 직장이 불안하고 안정되지 못하며 친척과의 인연이 좋지 않으며 시작은 좋은데 끝이 좋지 않다. ', '세파살_disp', '94');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '비염살(飛廉殺)이 들어오는 해이므로 정신적인 질병, 즉 스트레스 같은 질병이 노리고 있다.', '비염살_disp', '95');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '탕화살(湯火殺)이 들어오는 해이므로 끓는 물이나 불에 데이는 화상이나 부상을  조심하라.', '탕화살_disp', '96');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '삼재살(三災殺)중 들어올 삼재, 즉 들삼재의 해이니 화,수,풍의 자연재해나 나쁜 악사가 3 년동안 있게된다.', '삼재살1_disp', '97');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '삼재살(三災殺)중 들어와 누운 삼재, 즉 누울삼재의 해이니 화,수,풍의 자연재해나 나쁜 악사가 앞으로 2 년동안 있게된다. ', '삼재살2_disp', '98');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '삼재살(三災殺)중 나갈 삼재, 즉 날삼재의 해이니 화,수,풍의 자연재해나 나쁜 악사가 1 년동안 있게되면 이제 12 년뒤에 다시 온다.', '삼재살3_disp', '99');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '단교관살(斷橋關殺)이 들어오는 해이므로 말 그대로 교각이 끊어져 높은데서 추락한다는 암시이니 해외여행이나 비행기등 높은곳을 조심하라.', '단교관살_disp', '100');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '유하살(流霞殺)이 들어오는 해이므로 중풍이나 반신불수, 손발저림등의 흉한 병을 암시하고 여자이면 유산이나 자궁계의 질환에 유의하라.', '유하살_disp', '101');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '비인살(飛刃殺)이 들어오는 해이므로 이 비인살은 칼이 날아 다닌다는 암시이고 일의 시작은 있으니 끝이 없고 쓸데없는 참견으로 인한 시비, 투쟁으로 판단력이 흐려지고 분쟁만 일쌈는다는 흉살이다.', '비인살_disp', '102');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '음양착살(陰陽錯殺)이 들어오는 해이므로 남편은 아내를 의심하고 돈벌이를 게을리하고 여성은 남편때문에 눈물을 흘리는 암시로 부부간에 흉함을 나타내니 올한해는 서로 조심하라.', '음양착살_disp', '103');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '양인살(陽刃殺)이 들어오는 해이므로 양인살은 투쟁,고독,쾌락,탕진,이별등을 암시하는 매우 흉한 흉성이다. 조심하라.', '양인살_disp', '104');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '괴강살(魁강殺)이 들어오는 해이므로 앞뒤를 가리지 않는 흉살이다. 사주가 신강하면 발전적인 한해가 되겠지만 신약하면 이별,질병,단명,투쟁,찰과 등의 흉이 발동하는 무서운 흉살이다.', '괴강살_disp', '105');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '백호대살(白虎大殺)이 들어오는 해이므로 비명횡사를 암시하고 혈광사를 보게되며 부부인연이나 자식의 인연이 좋지 않다.', '백호대살_disp', '106');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '학당귀인(學堂貴人)이라는 길성이 들어오는 해이므로 학문을 하는 이에게 유리한 해로서 두뇌가 명석해지고 글재주가 뛰어나 사방에 이름을 날린다는것을 암시한다.', '학당귀인_disp', '107');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천주귀인(天廚貴人)이라는 길성이 들어오는 해이므로 명예를 얻고 재물을 모은다는 길신이다.', '천주귀인_disp', '108');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '금여록(金與祿)이라는 길성이 들어오는 해이므로 금으로 만든 가마를 타고 혼인하거나 행차를 한다는 암시이다.', '금여록_disp', '109');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '황은대사(皇恩大赦)라는 길성이 들어오는 해이므로 무거운 죄를 지었다 하드라도 귀인의 도움으로 감면 받거나 무사하다는 암시이다.', '황은대사_disp', '110');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천덕귀인(天德貴人) 이라는 길성이 들어오는 해로 하늘의 보살핌이 있으며 나쁜 흉성들의 휼작용을 무력화 시키는 힘이있다.', '천덕귀인_disp', '111');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '월덕귀인(月德貴人) 이라는 길성이 들어오는데 이귀인은 주로 부동산이나 땅을 소유한다는 암시가 있다.', '월덕귀인_disp', '112');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '천을귀인(天乙貴人)이라는 길성이 들어오는 해이므로 모든 흉살의 흉작용을 감소시키는 작용을 하는 최고의 길신이다.', '천을귀인_disp', '113');insert into S126(DB_check, DB_data, DB_express, DB_num) values('', '도화살(桃花煞)은 과도하고 잘못된 성욕으로 재앙을 당하기전에 여행, 취미생활, 운동 등으로 스트레스를 해소하여 음욕을 억제하여야 한다.', '도화살', '114');